import {
  faBed,
  faCalendarDays,
  faCar,
  faHouse,
  faPerson,
  faPlane,
  faTaxi,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./header.css";
import { DateRange } from "react-date-range";
import { useContext, useState, useRef } from "react";
import "react-date-range/dist/styles.css";
import "react-date-range/dist/theme/default.css";
import { format } from "date-fns";
import { Link, useNavigate } from "react-router-dom";
import { SearchContext } from "../../context/SearchContext";
import { AuthContext } from "../../context/AuthContext";

const Header = ({ type }) => {
  const [destination, setDestination] = useState("");
  const [openDate, setOpenDate] = useState(false);
  const [dates, setDates] = useState([
    {
      startDate: new Date(),
      endDate: new Date(),
      key: "selection",
    },
  ]);
  const [openOptions, setOpenOptions] = useState(false);
  const [options, setOptions] = useState({
    adult: 1,
    children: 0,
    room: 1,
  });
  const [searchResults, setSearchResults] = useState([]);

  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const dateRangeRef = useRef();

  const handleOption = (name, operation) => {
    setOptions((prev) => {
      return {
        ...prev,
        [name]: operation === "i" ? options[name] + 1 : options[name] - 1,
      };
    });
  };

  const { dispatch } = useContext(SearchContext);
  const database = [
    "Đà Nẵng",
    "Hà Nội",
    "Hồ Chí Minh",
    "Nha Trang",
    "Đà Lạt",
    "Huế",
    "Hội An",
  ];

  const handleSearch = () => {
    if (!destination.trim()) {
      alert("Vui lòng nhập địa điểm trước khi tìm kiếm!");
      return;
    }

    dispatch({
      type: "NEW_SEARCH",
      payload: { destination, dates, options },
    });
    navigate("/hotels", { state: { destination, dates, options } });
  };

  const handleCloseDateRange = () => {
    setOpenDate(false);
  };

  const handleCloseOptions = () => {
    setOpenOptions(false);
  };

  const handleInputChange = (event) => {
    const value = event.target.value;
    setDestination(value);

    const filteredResults = database.filter((item) =>
      item.toLowerCase().includes(value.toLowerCase())
    );

    setSearchResults(filteredResults);
  };

  const handleResultClick = (result) => {
    setDestination(result);
    setSearchResults([]);
  };

  return (
    <div className="header">
      <div
        className={
          type === "list" ? "headerContainer listMode" : "headerContainer"
        }
      >
        <div className="headerList">
          <div className="headerListItem">
            <FontAwesomeIcon icon={faHouse} />
            <span>
              <Link to="/" style={{ color: "unset", textDecoration: "none" }}>
                Trang chủ
              </Link>
            </span>
          </div>
        </div>
        {type !== "list" && (
          <>
            <h1 className="headerTitle">Đăng nhập để tiết kiệm</h1>
            <p className="headerDesc">
              Nhận phần thưởng cho chuyến du lịch của bạn - tiết kiệm ngay lập
              tức từ 10% trở lên với tài khoản HotelBooking miễn phí
            </p>

            <div className="headerSearch">
              <div className="headerSearchItem">
                <FontAwesomeIcon icon={faBed} className="headerIcon" />
                <input
                  type="text"
                  placeholder="Bạn đang ở đâu?"
                  className="headerSearchInput"
                  onChange={handleInputChange}
                  value={destination}
                />
                {searchResults.length > 0 && (
                  <ul className="searchResults">
                    {searchResults.map((result, index) => (
                      <li key={index} onClick={() => handleResultClick(result)}>
                        {result}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
              <div className="headerSearchItem" onClick={handleCloseOptions}>
                <FontAwesomeIcon icon={faCalendarDays} className="headerIcon" />
                <span
                  onClick={() => setOpenDate(!openDate)}
                  className="headerSearchText"
                >{`${format(dates[0].startDate, "MM/dd/yyyy")} to ${format(
                  dates[0].endDate,
                  "MM/dd/yyyy"
                )}`}</span>
                {openDate && (
                  <DateRange
                    ref={dateRangeRef}
                    editableDateInputs={true}
                    onChange={(item) => setDates([item.selection])}
                    moveRangeOnFirstSelection={false}
                    ranges={dates}
                    className="date"
                    minDate={new Date()}
                  />
                )}
              </div>
              <div className="headerSearchItem" onClick={handleCloseDateRange}>
                <FontAwesomeIcon icon={faPerson} className="headerIcon" />
                <span
                  onClick={() => setOpenOptions(!openOptions)}
                  className="headerSearchText"
                >{`${Math.min(40, options.adult)} người lớn · ${Math.min(
                  15,
                  options.children
                )} trẻ em · ${Math.min(30, options.room)} phòng`}</span>
                {openOptions && (
                  <div className="options">
                    <div className="optionItem">
                      <span className="optionText">Người lớn</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.adult <= 1}
                          className="optionCounterButton"
                          onClick={() => handleOption("adult", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {Math.min(40, options.adult)}
                        </span>
                        <button
                          className="optionCounterButton"
                          onClick={() => handleOption("adult", "i")}
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <div className="optionItem">
                      <span className="optionText">Trẻ em</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.children <= 0}
                          className="optionCounterButton"
                          onClick={() => handleOption("children", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {Math.min(15, options.children)}
                        </span>
                        <button
                          className="optionCounterButton"
                          onClick={() => handleOption("children", "i")}
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <div className="optionItem">
                      <span className="optionText">Phòng</span>
                      <div className="optionCounter">
                        <button
                          disabled={options.room <= 1}
                          className="optionCounterButton"
                          onClick={() => handleOption("room", "d")}
                        >
                          -
                        </button>
                        <span className="optionCounterNumber">
                          {Math.min(30, options.room)}
                        </span>
                        <button
                          className="optionCounterButton"
                          onClick={() => handleOption("room", "i")}
                        >
                          +
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
              <div className="headerSearchItem">
                <button className="headerBtn" onClick={handleSearch}>
                  Tìm kiếm
                </button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Header;
