import React, { useContext, useState } from "react";
import "./login.css"; // Import your custom CSS file
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [credentials, setCredentials] = useState({
    username: undefined,
    password: undefined,
  });

  const { loading, error, dispatch } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();
    dispatch({ type: "LOGIN_START" });
    try {
      const res = await axios.post("/auth/login", credentials);
      dispatch({ type: "LOGIN_SUCCESS", payload: res.data });
      navigate("/");
    } catch (err) {
      dispatch({ type: "LOGIN_FAILURE", payload: err.response.data });
    }
  };

  return (
    <div className="login-container">
      {" "}
      {/* Use a custom class for styling */}
      <div className="login-form">
        {" "}
        {/* Nested container for form elements */}
        <h1 className="login-title">Login</h1> {/* Title with custom class */}
        <form onSubmit={handleClick}>
          <div className="input-container">
            {" "}
            {/* Container for each input field */}
            <label htmlFor="username" className="input-label">
              Username
            </label>
            <input
              type="text"
              id="username"
              className="login-input"
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-container">
            <label htmlFor="password" className="input-label">
              Password
            </label>
            <input
              type="password"
              id="password"
              className="login-input"
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? "Loading..." : "Login"}
          </button>

          {error && <p className="error-message">{error.message}</p>}
        </form>
      </div>
      {/* Background image section (optional)  */}
      <div className="login-background">
        <img
          src="https://e1.pxfuel.com/desktop-wallpaper/581/154/desktop-wallpaper-backgrounds-for-login-page-login-page.jpg" // Replace with your image path
          alt="Login background"
          className="background-image"
        />
      </div>
    </div>
  );
};

export default Login;
