import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./review.css";
import { faStar } from "@fortawesome/free-solid-svg-icons";

const Review = () => {
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    feedback: "",
    star: 1, // Mặc định là 1 sao
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const response = await fetch("/api/reviews", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Xử lý khi tạo review thành công
        console.log("Review created successfully!");
      } else {
        // Xử lý khi có lỗi xảy ra
        console.error("Failed to create review");
      }
    } catch (error) {
      console.error("Failed to create review:", error);
    }
  };

  return (
    <div className="review">
      <div className="titleReview">
        <h2>Đánh giá khách hàng</h2>
      </div>
      <div className="containerReviews">{/* Phần hiển thị reviews */}</div>
      <div className="writeReview">
        <h3 style={{ marginBottom: "20px" }}>Write a review</h3>
        <div className="containerComment">
          <div className="feedback">
            <label>Feedback</label>
            <textarea
              id="feedback"
              name="feedback"
              className="inputFeedback"
              placeholder="Viết đánh giá tại đây..."
              value={formData.feedback}
              onChange={handleChange}
            ></textarea>
          </div>
          <div className="inputInfo">
            <div className="infInput">
              <label htmlFor="username">Họ tên</label>
              <input
                type="text"
                id="username"
                name="username"
                className="inputText"
                placeholder="Họ và tên"
                value={formData.username}
                onChange={handleChange}
              />
            </div>
            <div className="infInput">
              <label htmlFor="email">Email</label>
              <input
                type="text"
                id="email"
                name="email"
                className="inputText"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
              />
            </div>
            <div className="infInput">
              <label htmlFor="star">Rating</label>
              <select
                id="star"
                name="star"
                className="inputRating"
                value={formData.star}
                onChange={handleChange}
              >
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
              </select>
            </div>
          </div>
        </div>
      </div>
      <div className="buttonHandle">
        <button type="button" className="submit" onClick={handleSubmit}>
          Submit
        </button>
        {/* Button "Cancel" */}
      </div>
    </div>
  );
};

export default Review;
