import { useContext, useState } from "react";
import "./register.css";
import { AuthContext } from "../../context/AuthContext";
import { useNavigate } from "react-router-dom";
import Navbar from "./../../components/navbar/Navbar";

const Register = () => {
  const [credentials, setCredentials] = useState({
    username: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState(""); // Thêm state để lưu trữ thông báo lỗi
  const { loading, register } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleClick = async (e) => {
    e.preventDefault();
    // Kiểm tra validate trước khi gửi thông tin đăng ký
    const validationError = validate(credentials);
    if (validationError) {
      setError(validationError);
      return;
    }
    const res = await register(credentials);
    if (res) {
      navigate("/login");
    }
  };

  // Hàm validate
  const validate = (values) => {
    if (
      !values.username ||
      !values.email ||
      !values.password ||
      !values.confirmPassword
    ) {
      return "Please fill in all fields.";
    }
    if (values.password !== values.confirmPassword) {
      return "Passwords do not match.";
    }
    return "";
  };

  return (
    <>
      <Navbar />
      <div className="register-page">
        <div className="register">
          <div className="rContainer">
            <div className="rHeader">Register</div>
            <input
              type="text"
              placeholder="Username"
              id="username"
              onChange={handleChange}
              className="rInput"
              required
            />
            <input
              type="email"
              placeholder="Email"
              id="email"
              onChange={handleChange}
              className="rInput"
              required
            />
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              id="password"
              onChange={handleChange}
              className="rInput"
              required
            />
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Confirm Password"
              id="confirmPassword"
              onChange={handleChange}
              className="rInput"
              required
            />
            <div className="show-password-container">
              <input
                style={{ height: "20px" }}
                type="checkbox"
                id="showPassword"
                checked={showPassword}
                onChange={handleShowPassword}
              />
              <label htmlFor="showPassword">Show Password</label>
            </div>
            <button
              disabled={loading}
              onClick={handleClick}
              className="rButton"
            >
              Register
            </button>
            {error && <span>{error}</span>} {/* Hiển thị thông báo lỗi */}
          </div>
        </div>
      </div>
    </>
  );
};

export default Register;
