import { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./review.css";
import { faStar } from "@fortawesome/free-solid-svg-icons";

const Review = () => {
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch("/api/reviews/all");
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };

    fetchReviews();
  }, []);

  return (
    <div className="review">
      <div className="titleReview">
        <h2>Đánh giá khách hàng</h2>
      </div>
      <div className="containerReviews">
        {reviews.map((review, index) => (
          <div key={index} className="cardReview">
            <div className="boxReview">
              <div className="revCustomer">
                <h4 className="cusName">{review.username}</h4>
                <span className="ratingNumber">
                  {review.star}/5 <FontAwesomeIcon icon={faStar} />
                </span>
              </div>
              <div className="descReview">{review.feedback}</div>
            </div>
          </div>
        ))}
      </div>
      <div className="writeReview">
        <h3 style={{ marginBottom: "20px" }}>Write a review</h3>
        <div className="containerComment">
          <div className="feedback">
            <label>Feedback</label>
            <textarea
              id="feedback"
              className="inputFeedback"
              placeholder="Viết đánh giá tại đây..."
            />
          </div>
          <div className="inputInfo">
            <div className="infInput">
              <label htmlFor="">Họ tên</label>
              <input
                type="text"
                className="inputText"
                placeholder="Họ và tên"
              />
            </div>
            <div className="infInput">
              <label htmlFor="">Email</label>
              <input type="text" className="inputText" placeholder="Email" />
            </div>
          </div>
        </div>
      </div>
      <div className="buttonHandle">
        <button type="button" className="submit">
          Submit
        </button>
        <button type="button" className="cancel">
          Cancel
        </button>
      </div>
    </div>
  );
};

export default Review;
